/**
 * Function that is called during the JavaScript Task execution.
 * @param {IntegrationEvent} event
 */
function executeScript(event) {

    var jsonData = [
    {
        "role": "user",
        "parts": [
            {
                "text": "User prompt is:\nPlease identify the PoS document."
            },
            {
                "fileData": {
                    "mimeType": "application/pdf",
                    "fileUri": "NEW_FILE_URI"
                }
            },
            {
                "text": "Respond only as [Green, Blue, Miscellaneous, Incorrect Documentation]"
            }
        ]
    }
    ];

    var prompt_input_file_uri = event.getParameter("prompt-input-file-uri");
    var content_string = JSON.stringify(jsonData).replace("NEW_FILE_URI", prompt_input_file_uri);
    event.setParameter("request_content_tmp",content_string);
}

/**
 * Function that is called during the JavaScript Task execution.
 * @param {IntegrationEvent} event
 */
function executeScript(event) {

  var jsonString =  event.getParameter("pos-response-body-str");
  jsonString = JSON.parse(jsonString)[0];
  var parsedData = JSON.parse(jsonString);
  var extractTextFields = (data) => {
  var texts = [];
    data.forEach(item => {
        item.candidates.forEach(candidate => {
        if (candidate.content && candidate.content.parts) {
            candidate.content.parts.forEach(part => {
            if (part.text !== undefined) {
                texts.push(part.text);
            }
            });
        }
        });
    });

    return texts;
    };

    var textFields = extractTextFields(parsedData);
    event.setParameter("pos-response-result",textFields[0]);

}

